import React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import AdminNav from './AdminNav';
import JobCard from './AdminJobCard';
import jwt from 'jsonwebtoken';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';

function AdminBoard() {
  const [email, setEmail] = useState('');

  const nav = useNavigate();

  useEffect(() => {
    const checkAuthorization = () => {
      console.log('AUTHENTICATING');
      const token = localStorage.getItem('token');
      if (token) {
        const admin = jwt.decode(token);
        console.log('USER WHILE AUTHENTICATING : ', admin);
        if (!admin || !admin.isAdmin) {
          localStorage.removeItem('token');
          nav('/admin-login');
        } else {
          setEmail(admin.email);
        }
      } else nav('/admin-login');
    };
    checkAuthorization();
  }, [nav]);

  const [jobCards, setJobCards] = useState([]);

  useEffect(() => {
    async function fetchJobs() {
      const res = await axios.get('http://localhost:3000/admin/jobs');
      const jobs = res.data.jobs;

      setJobCards(
        jobs.map((job, index) => (
          <JobCard
            key={job.id}
            index={index}
            id={job.id}
            title={job.title}
            location={job.location}
            deadline={job.deadline}
            contact={job.contact}
            email={email}
            archived={job.archived}
          />
        ))
      );
    }

    fetchJobs();
  }, [email]);

  function handleDragEnd(result) {
    if (!result.destination) return;

    const items = Array.from(jobCards);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setJobCards(items);
  }

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <div>
        <AdminNav />
        <Droppable droppableId="jobCards">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {jobCards}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </div>
    </DragDropContext>
  );
}

export default AdminBoard;
