  return (
    <div>
      <Link to={`/job-applicants/${id}`}>
        <div className='job-card'>
          <h2>{title}</h2>
          <p>Id: {id}</p>
          <p>Location: {location}</p>
          <p>Deadline: {deadline}</p>
          <p>Contact: {contact}</p>
        </div>
      </Link>
    </div>
  )
}

export default JobCard
