import React from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import { useState, useEffect } from 'react'
import NavBar from './NavBar'
import JobCard from './JobCard'
import jwt from 'jsonwebtoken'
import './cssfiles/jobboard.css'
import loadingimage from '../../loading.gif'

function JobBoard() {
  const [email, setEmail] = useState('')
  const nav = useNavigate()
  const [jobCards, setJobCards] = useState([])
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    const checkAuthorization = async () => {
      console.log('AUTHENTICATING')
      const token = localStorage.getItem('token')
      if (token) {
        const user = jwt.decode(token)
        console.log('USER WHILE AUTHENTICATING : ', user)
        if (!user) {
          localStorage.removeItem('token')
          nav('/login')
        } else {
          setEmail(user.email)
        }
      } else nav('/login')
    }

    const fetchData = async () => {
      setLoading(true)

      try {
        const jobsResponse = await axios.get('https://linkedoutbackend.onrender.com/user/jobs')
        const jobs = jobsResponse.data.jobs
        const orderResponse = await axios.get('https://linkedoutbackend.onrender.com/admin/getorder')
        const order = orderResponse.data.order
        const appliedJobsResponse = await axios.post('https://linkedoutbackend.onrender.com/user/applied-jobs', { email })

        const appliedJobIds = appliedJobsResponse.data.appliedJobs.map(job => job.id)
        const unappliedJobs = jobs
          .filter(job => !appliedJobIds.includes(job.id))
          .sort((job1, job2) => order.indexOf(job1.id) - order.indexOf(job2.id))

        setJobCards(unappliedJobs.map(job => (
          <JobCard
            id={job.id}
            title={job.title}
            location={job.location}
            deadline={job.deadline}
            contact={job.contact}
            email={email}
          />
        )))
      } catch (error) {
        console.error(error)
      }

      setLoading(false)
    }

    checkAuthorization()
    fetchData()
  }, [email, nav])

  if (loading) {
    return (
      <div className="jobboard">
        <NavBar />
        <img src={loadingimage} alt="Loading..." style={{ marginTop: "17%", marginLeft: "49%" }} />
      </div>
    )
  }

  return (
    <div className="jobboard">
      <NavBar />
      <div id="cardholder">{jobCards}</div>
    </div>
  )
}

export default JobBoard
