import React from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import { useState, useEffect } from 'react'
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import AdminNav from './AdminNav'
import JobCard from './AdminJobCard'
import jwt from 'jsonwebtoken'

function AdminBoard () {
  const [email, setEmail] = useState('')
  const [jobs, setJobs] = useState([]);

  const nav = useNavigate()

  useEffect(() => {
    const checkAuthorization = () => {
      console.log('AUTHENTICATING')
      const token = localStorage.getItem('token')
      if (token) {
        const admin = jwt.decode(token)
        console.log('USER WHILE AUTHENTICATING : ', admin)
        if (!admin || !admin.isAdmin) {
          localStorage.removeItem('token')
          nav('/admin-login')
        } else {
          setEmail(admin.email)
        }
      } else nav('/admin-login')
    }
    checkAuthorization()
  }, [nav])

  useEffect(() => {
    async function fetchJobs () {
      const res = await axios.get('http://localhost:3000/admin/jobs')
      const jobs = res.data.jobs
      setJobs(jobs);
    } 

    fetchJobs()
  }, [])

  const handleDragEnd = (result) => {
    if (!result.destination) {
      return;
    }

    const updatedJobs = Array.from(jobs);
    const [reorderedJob] = updatedJobs.splice(result.source.index, 1);
    updatedJobs.splice(result.destination.index, 0, reorderedJob);
    setJobs(updatedJobs);
  };

  return (
    <div>
      <AdminNav />
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="jobCards">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {jobs.map((job, index) => (
                <Draggable key={job.id} draggableId={job.id} index={index}>
                  {(provided) => (
                    <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                      <JobCard
                        id={job.id}
                        title={job.title}
                        location={job.location}
                        deadline={job.deadline}
                        contact={job.contact}
                        email={email}
                        archived={job.archived}
                      />
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  )
}

export default AdminBoard;
