import React, { useState } from 'react';
import AdminNav from './AdminNav';
import {useNavigate} from 'react-router-dom';
import { toast } from 'react-toastify'



const CreateJob = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [location, setLocation] = useState('');
  const [contact, setContact] = useState('');
  const nav=useNavigate();

  async function AddJob (
    title,
    description,
    deadline,
    location,
    contact
  ) {
    const res = await fetch('http://localhost:3000/admin/createjob', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        id: req.body.id,
        title: req.body.title,
        description: req.body.description,
        location: req.body.location,
        deadline: req.body.deadline,
        contact: req.body.contact,
        appliedJobs: []
      })
    })

    const data = await res.json()
    console.log(data)

    if (data.status === 'ok') {
      toast.success('Job created successfully!', { theme: 'colored' })
      // Redirect the user to the sign-in page
      nav('/admin-board')
    } else {
      toast.error('Failed to create job', { theme: 'colored' })
    }
  }

  async function handleSubmit (event) {
    event.preventDefault()

    console.log('SUBMITTED')
    toast.success('Job Added', { theme: 'colored' })

    AddJob(title, description, deadline, location, contact)
  }

//   const handleSubmit = (e) => {
//     e.preventDefault();
//     // Do something with the form data, like send it to a server

//     console.log({
//       title,
//       description,
//       deadline,
//       location,
//       contact,
//     });
//   };

  return (
    <div>
    <AdminNav />
    <form onSubmit={handleSubmit}>
      <label htmlFor="title">Job Title:</label>
      <input
        type="text"
        id="title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />

      <label htmlFor="description">Job Description:</label>
      <textarea
        id="description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      ></textarea>

      <label htmlFor="deadline">Deadline:</label>
      <input
        type="date"
        id="deadline"
        value={deadline}
        onChange={(e) => setDeadline(e.target.value)}
      />

      <label htmlFor="location">Location:</label>
      <input
        type="text"
        id="location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />

      <label htmlFor="contact">Contact:</label>
      <input
        type="text"
        id="contact"
        value={contact}
        onChange={(e) => setContact(e.target.value)}
      />

      <button type="submit">AddJob</button>
    </form>
    </div>
  );
};

export default CreateJob;
