import React, { useState } from "react";

function Profile({ name, dob, email, phone, qualifications, experience }) {
  const [editedName, setEditedName] = useState(name);
  const [editedDob, setEditedDob] = useState(dob);
  const [editedEmail, setEditedEmail] = useState(email);
  const [editedPhone, setEditedPhone] = useState(phone);
  const [editedQualifications, setEditedQualifications] = useState(qualifications);
  const [editedExperience, setEditedExperience] = useState(experience);
  const [isEditing, setIsEditing] = useState(false);

  const handleUpdate = () => {
    setIsEditing(true);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsEditing(false);
  };

  return (
    <div>
      <h1>Profile Page</h1>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            value={editedName}
            onChange={(event) => setEditedName(event.target.value)}
          />
          <br />
          <label htmlFor="dob">Date of Birth:</label>
          <input
            type="date"
            id="dob"
            value={editedDob}
            onChange={(event) => setEditedDob(event.target.value)}
          />
          <br />
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={editedEmail}
            onChange={(event) => setEditedEmail(event.target.value)}
          />
          <br />
          <label htmlFor="phone">Phone Number:</label>
          <input
            type="tel"
            id="phone"
            value={editedPhone}
            onChange={(event) => setEditedPhone(event.target.value)}
          />
          <br />
          <label htmlFor="qualifications">Qualifications:</label>
          <textarea
            id="qualifications"
            value={editedQualifications}
            onChange={(event) => setEditedQualifications(event.target.value)}
          ></textarea>
          <br />
          <label htmlFor="experience">Experience:</label>
          <textarea
            id="experience"
            value={editedExperience}
            onChange={(event) => setEditedExperience(event.target.value)}
          ></textarea>
          <br />
          <button type="submit">Save</button>
        </form>
      ) : (
        <div>
          <p>Name: {editedName}</p>
          <p>Date of Birth: {editedDob}</p>
          <p>Email: {editedEmail}</p>
          <p>Phone Number: {editedPhone}</p>
          <p>Qualifications: {editedQualifications}</p>
          <p>Experience: {editedExperience}</p>
          <button onClick={handleUpdate}>Update</button>
        </div>
      )}
    </div>
  );
}

export default Profile;
