import React from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import { useState, useEffect } from 'react'
import AdminNav from './AdminNav'
import JobCard from './AdminJobCard'
import jwt from 'jsonwebtoken'
// import { DragDropContext , Droppable , Draggable} from 'react-beautiful-dnd';


function AdminBoard () {
  const [email, setEmail] = useState('')

  const nav = useNavigate()

  useEffect(() => {
    const checkAuthorization = () => {
      console.log('AUTHENTICATING')
      const token = localStorage.getItem('token')
      if (token) {
        const admin = jwt.decode(token)
        console.log('USER WHILE AUTHENTICATING : ', admin)
        if (!admin || !admin.isAdmin) {
          localStorage.removeItem('token')
          nav('/admin-login')
        } else {
          setEmail(admin.email)
        }
      } else nav('/admin-login')
    }
    checkAuthorization()
  }, [nav])

  const [jobCards, setJobCards] = useState([])

  // function handleOnDragEnd(result) {
  //   if (!result.destination) return;

  //   const items = Array.from(jobdatas);
  //   const [reorderedItem] = items.splice(result.source.index, 1);
  //   items.splice(result.destination.index, 0, reorderedItem);

  //   updateCharacters(items);
  // }


  useEffect(() => {
    async function fetchJobs () {
      const res = await axios.get('http://localhost:3000/admin/jobs')
      const jobs = res.data.jobs

      // appliedJobs=jobs-appliedJobs
      
      setJobCards(
        jobs.map(job => (
          <JobCard
            id={job.id}
            title={job.title}
            location={job.location}
            deadline={job.deadline}
            contact={job.contact}
            email={email}
          />
        ))
      )
      

    } 

    fetchJobs()
  }, [email])

  return (
    <div>
      <AdminNav />
      <div>
        {/* <DragDropContext>
        {(provided) => (
          <ul className="jobdatas" {...provided.droppableProps} ref={provided.innerRef} > */}
            {jobCards}
          {/* </ul>
        )}
        </DragDropContext> */}
      </div>
    </div>
  )
}

export default AdminBoard
