import React from "react";
import { useParams } from "react-router-dom";
import { useEffect, useState} from 'react';
import axios from 'axios';

function JobDescription() {
  const { id } = useParams();

  const [description, setDescription] = useState("");

 

  
  async function fetchJobDesc(){
    const res = await axios.post("http://localhost:3000/user/job-description", {id: id});
    setDescription(res.data.description);
  }
  
  useEffect(()=>{
    fetchJobDesc()
  },[]);

  // fetch job description using id from API or other data source

  return (
    <div>
      <h1>Job Description for Job {id}</h1>
      <p>This is the description for job {id}.</p>
      <p> {description}</p>
    </div>
  );
}

export default JobDescription;
