import React from 'react'
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom'
import {useState, useEffect} from 'react';
import NavBar from './NavBar'
import JobCard from './JobCard'
import jwt from "jsonwebtoken";

function AppliedJobs () {

  const nav = useNavigate();

  const [email, setEmail] = useState("");

  useEffect(() => {
    const checkAuthorization = () => {
      console.log("AUTHENTICATING");
      const token = localStorage.getItem("token");
      if (token) {
        const user = jwt.decode(token);
        console.log("USER WHILE AUTHENTICATING : ", user);
        if (!user) {
          localStorage.removeItem("token");
          nav("/login");
        }
        else {
            setEmail(token.email);
        }
      }
      else nav("/login");
    };
    checkAuthorization();
  }, [nav]);

  const [appliedJobCards, setAppliedJobCards] = useState([]);




  useEffect(() => {
    async function fetchAppliedJobs() {
        const res = await axios.post("http://localhost:3000/user/applied-jobs", {email: email});
        const appliedJobs = res.data.appliedJobs;
        setAppliedJobCards(appliedJobs.map(job =>    <JobCard
        id={job.id}
        title={job.title}
        location={job.location}
        deadline={job.deadline}
        contact={job.contact}
        />));
    }
    if (email !== "") fetchAppliedJobs();
  }, [email]);

  return (
    <div>
      <NavBar />
      <div>{appliedJobCards}</div>
    </div>
  )
}

export default AppliedJobs
